import React from "react";
import styled from "styled-components";
import ImgSlider from "../../mainslider/ImgSlider";

export default function AboutWebsite() {
  let data = [
    {
      img: "/AboutHospitalImages/Doctor1.jpg",
      discription: "Vice Chancellor",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/AboutHospitalImages/Doctor2.jpg",
      discription: "Pro Vice Chancellor",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/AboutHospitalImages/Doctor3.jpg",
      discription: "Head Of DepartmentS",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/AboutHospitalImages/Doctor4.jpg",
      discription: "Head Of Faculty",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/AboutHospitalImages/Doctor5.jpg",
      discription: "Head Of Worker",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
  ];

  let slidersetting = {
    slidetoshow: 3,
  };

  return (
    <AboutWebsiteContainer>
      <ImgSlider data={data} slidersetting={slidersetting} />
      <div>
        <MainContainer>
          <HistoryOfHospital>
            <HeadingContainer>
              <p>History Of Hospital</p>
            </HeadingContainer>
            <DetailContainer>
              <p>
                International Medical Network Medicana is one of the largest
                networks of medical institutions in the region. Medicana
                hospitals group occupy leading positions among the most
                high-tech medical centers in Turkey. Quality and safety of
                services in institutions of Medicana complies with the latest
                international health standards and the level of centers are
                compared with the top world leaders in medicine. First clinic
                was founded in 1995, today the Medicana network consist of 9
                modern medical centers (two of which are highly advanced Dental
                centers), specialized institutions and research departments. The
                leading clinics of the group are situated in Istanbul, Ankara,
                Konya and Samsun. People come to Medicana seeking for treatment
                in fields of neurosurgery, transplantology, oncology, heart
                surgery, obstetrics, gynecology, and so on.
              </p>
            </DetailContainer>
          </HistoryOfHospital>
        </MainContainer>
      </div>
    </AboutWebsiteContainer>
  );
}

const AboutWebsiteContainer = styled.div`
  position: relative;
  top: 60px;
`;

const MainContainer = styled.div`
  width: 100%;
  position: relative;
  left: 0px;
  top: 40px;
  text-align: center;
  
`;

const HistoryOfHospital = styled.div`
  box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  border-radius: 5px;
  background-color: white;
`;

const HeadingContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;

  p {
    text-align: center;
    font-family: "Bree Serif", serif;
    font-size: 1.8rem;
    font-weight: bold;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
    background-color: white;
    color: #090b13;
    padding: 4px;
    margin-top: 30px;
    margin-bottom: 10px;
    border-radius: 5px;
  }
`;


const DetailContainer=styled.div`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  margin: 2%;
  box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  border-radius: 5px;
  padding: 3px;

  p {
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.5rem;
    line-height: 1.4rem;
  }
`
