    },
  ];

  let ImgUrlsForSideBar = [
    {
      id: 1,
      child: (
        <>
          <p className="flex mx-1 w-18">Test</p>
          <img
            className="w-12 ImgContainer"
            src="/ImagesForTreatmentPage/test.png"
