import React from "react";
import styled from "styled-components";

export default function SlideComponent() {
  return (
    <SideContainer>
      <button>
        <div className="HoveredDiv">
          <div className="ImagecontainerDiv">
            <img src="/Images/facebook.png" alt="MyHospitalWeb" />
          </div>
        </div>
      </button>
      {/* <button>
        <span>Option 2</span>
