      display: flex;
      .ImagecontainerDiv {
        border: 2px solid crimson;
        width: 100%;
        height: 100%;
        border-radius: 50%;
        img {
          width: 100%;
          height: 100%;
          filter: invert(100%);
        }
      }
      &::before{
        content: "pankaj";
        color: white;
      }
