import React from "react";
import styled from "styled-components";

export default function DonationForm() {
  const scroll_right = () => {
    var rightsidescoll = document.querySelector("#scroll_left_and_right_side");
    rightsidescoll.scrollBy(1009, 0);
  };

  const scroll_left = () => {
    var leftsidescoll = document.querySelector("#scroll_left_and_right_side");
    leftsidescoll.scrollBy(-1009, 0);
  };

  return (
    <MainContainer>
      <div class="main_container">
        <div className="ImageContainer">
          <img
            src="/DonateImages/left-arrow.png"
            alt="MyHospitalWebsiteApp"
            onClick={scroll_left}
          />
        </div>
        <div class="main-scroll-div">
          <div class="cover">
            <div class="scroll-images" id="scroll_left_and_right_side">
              <div class="child">
                <FormContainer>
                  <div className="FormMainContainer">
                    <div className="FromImageContainer">
                      <img
                        src="/DonateImages/BloodDontaionFormImage.png"
                        alt="HospitalWebApplication"
                      />
                    </div>
                    <div className="BloodDonationDetailsContainer">
                      <div className="HeadingContainer">
                        <h2>Blood Donation Form</h2>
                      </div>
                      <div className="DetailsAboutBloodDonation">
                        <p>
                          Lorem ipsum dolor sit amet consectetur adipisicing
                          elit. Dicta minima sint suscipit, voluptatibus aperiam
                          nihil! Alias deserunt animi similique voluptatum sed,
                          blanditiis quibusdam nam consectetur autem molestiae
                          veritatis repellat sint ut quia modi ab?
                        </p>
                      </div>
                    </div>
                  </div>

                  <div className="FormDetailsContainer">
                    <form>
                      <input type="text" placeholder="First Name" />
                      <input type="text" placeholder="Second Name" />
                    </form>
                  </div>
                </FormContainer>
              </div>
            </div>
          </div>
        </div>
        <div className="ImageContainer">
          <img
            src="/DonateImages/Right-Arrow.png"
            alt="MyHospitalWebsiteApp"
            onClick={scroll_right}
          />
        </div>
      </div>
    </MainContainer>
  );
}

const MainContainer = styled.section`
  .ImageContainer {
    width: 7%;
    height: 100%;
    border-radius: 50%;
    transition: all 0.3s ease-in-out;
  }
  .ImageContainer:hover {
    cursor: pointer;
    transform: scale(1.08);
  }

  .main_container {
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .main-scroll-div {
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: space-between;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
    background-color: rgb(255, 255, 255);
    color: #090b13;
  }
  .cover {
    position: relative;
    width: 100%;
    height: 100%;
  }

  .scroll-images {
    width: 100%;
    height: auto;
    display: flex;
    justify-content: left;
    align-items: center;
    overflow: auto;
    position: relative;
    scroll-behavior: smooth;
  }

  .scroll-images::-webkit-scrollbar {
    -webkit-appearance: none;
  }

  .child {
    min-width: 100%;
    height: 100%;
    margin: 1px 1px;
    overflow: hidden;
    display: flex;
    flex-direction: column;
    align-items: center;
    align-content: center;
  }
`;

// Blood Donation Form Css
const FormContainer = styled.div`
  width: 100%;

  .FormMainContainer {
    display: flex;
    align-items: center;
    justify-content: space-between;
    border: 2px solid green;
  }
  .FromImageContainer {
    border: 2px solid crimson;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 10%;
    img {
      width: 100%;
      height: 100%;
      border: 2px solid pink;
    }
  }
  .BloodDonationDetailsContainer {
    border: 2px solid blue;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 90%;
  }
  .HeadingContainer {
    border: 2px solid green;

    font-size: 1.5rem;
    font-weight: bold;
    font-family: "Open Sans", sans-serif;
    text-align: center;
    color: #090b13;
    word-spacing: 0.3rem;
    line-height: 1.4rem;
    padding: 8px;
  }
  .DetailsAboutBloodDonation {
    border: 2px solid magenta;
    font-size: 1.5rem;
    font-weight: bold;
    font-family: "Open Sans", sans-serif;
    text-align: center;
    color: green;
    word-spacing: 0.3rem;
    line-height: 1.4rem;
    padding: 8px;
  }
`;
