import React from "react";
import styled from "styled-components";

export default function TreatmentDetailsContainer() {
  let details = [
    {
      TreatmentHeading: "Types Of Treatment",
      Treatment1: "Curative Treatment",
      Treamtent1Detail:
        "Curative or therapeutic care refers in part to treatments and therapies provided to a patient with the goal of curing an illness or condition. The terms are also used for treatments that delay disease progression even when a cure is not possible.",
      ExampleOfTreatmentAvailableHeading:
        "Example Of Curative Treamtent Available In Our Hospital",
      ExampleOfTreatmentAvailable: [
        {
          treatment: "Cancer Curative Treatment",
        },
        {
          treatment: "Sar Dard",
        },
        {
          treatment: "Stomatch Pain",
        },
        {
          treatment: "Brain",
        },
      ],
      // data: [
      //   {
      //     img: "/Images/workforyou1.jpg",
      //     disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
      //   },
      //   {
      //     img: "/Images/workforyou2.jpg",
      //     disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
      //   },
      //   {
      //     img: "/Images/workforyou3.jpg",
      //     disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
      //   },
      //   {
      //     img: "/Images/workforyou4.jpg",
      //     disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
      //   },
      // ],
    },
  ];

  return (
    <MainContainer>
      <TreatmentPageContainers>
        <NgoHeadingContainers>prithvi singh raikwal</NgoHeadingContainers>
      </TreatmentPageContainers>
    </MainContainer>
  );
}

const MainContainer = styled.div`
  width: 90%;
  position: relative;
  left: 80px;
  top: 40px;
  text-align: center;
  z-index: -1;
`;

const NgoHeadingContainers = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 3%;

  p {
    text-align: center;
    font-family: "Bree Serif", serif;
    font-size: 1.5rem;
    color: white;
    border-radius: 5px;
    font-weight: bold;
    display: inline-flex;
    padding: 4px;
    margin-top: 30px;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
    border-radius: 5px;
    background-color: white;
    color: #090b13;
    /* box-shadow: 0px 0px 15px 4px #090b13; */
    /* background-color: #090b13; */
  }
`;

const TreatmentPageContainers = styled.div`
  box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  border-radius: 5px;
  background-color: white;
`;
