import MedicalIssueContainer from "./MedicalIssueContainer";
import MentalHealthContainer from "./MentalHealthContainer";
import SurgicalProcedureContainer from "./SurgicalProcedureContainer";
import CareAndSupportContainer from "./CareAndSupportContainer";

// import RegisterForAppointment from "../RegisterForAppointment";

export default function TreatmentMainPage() {
  const [Container, setContainer] = useState("TreatmentDetailContainer");

  let slidersetting = {
    slidetoshow: 3,
