import React from "react";
import styled from "styled-components";

export default function TreatmentDetailsContainer() {
  let details = [
    {
      TreatmentHeading: "Types Of Treatment",
      Treatment1: "Curative Treatment",
      Treamtent1Detail:
        "Curative or therapeutic care refers in part to treatments and therapies provided to a patient with the goal of curing an illness or condition. The terms are also used for treatments that delay disease progression even when a cure is not possible.",
      TreatmentAvailable:
        "Example Of Curative Treamtent Available In Our Hospital",
      // data: [
      //   {
      //     img: "/Images/workforyou1.jpg",
      //     disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
      //   },
      //   {
      //     img: "/Images/workforyou2.jpg",
      //     disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
      //   },
      //   {
      //     img: "/Images/workforyou3.jpg",
      //     disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
      //   },
      //   {
      //     img: "/Images/workforyou4.jpg",
      //     disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
      //   },
      // ],
    },
  ];
  return (
    <MainContainer>
      {details.map((item, index) => {
        return (
          <TreatmentDetails>
            <TreatmentHeadingContainers>
              <p key={index}>{item.TreatmentHeading}</p>
            </TreatmentHeadingContainers>
            <TypesOfTreatment>
              <p key={index}>{item.Treatment1}</p>
            </TypesOfTreatment>

            <TreatmentInfoContainer>
              <TreatmentInformation>
                <p key={index}>{item.Treamtent1Detail}</p>
              </TreatmentInformation>
              <ExampleOfTreatment>
                <p key={index}>{item.TreatmentAvailable}</p>
              </ExampleOfTreatment>
            </TreatmentInfoContainer>
          </TreatmentDetails>
        );
      })}
    </MainContainer>
  );
}

const MainContainer = styled.div`
  width: 90%;
  position: relative;
  left: 80px;
  top: 40px;
  text-align: center;
  z-index: -1;
`;

const TreatmentDetails = styled.div`
  box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  border-radius: 5px;
  background-color: white;
`;

const TreatmentHeadingContainers = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 3%;

  p {
    text-align: center;
    font-family: "Bree Serif", serif;
    font-size: 1.5rem;
    color: white;
    border-radius: 5px;
    font-weight: bold;
    display: inline-flex;
    padding: 4px;
    margin-top: 30px;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
    border-radius: 5px;
    background-color: white;
    color: #090b13;
    /* box-shadow: 0px 0px 15px 4px #090b13; */
    /* background-color: #090b13; */
  }
`;

const TypesOfTreatment = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  margin: 3%;
  border: 2px solid crimson;
  p {
    text-align: center;
    font-family: "Bree Serif", serif;
    font-size: 1.5rem;
    color: white;
    border-radius: 5px;
    font-weight: bold;
    display: inline-flex;
    padding: 4px;
    margin-top: 30px;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
    border-radius: 5px;
    background-color: white;
    color: #090b13;
    /* box-shadow: 0px 0px 15px 4px #090b13; */
    /* background-color: #090b13; */
  }
`;

const TreatmentInfoContainer = styled.div`
  border: 2px solid crimson;
  display: flex;
  flex-direction: row;
`;
const TreatmentInformation = styled.div`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  margin: 3%;
  border: 2px solid green;

  p {
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.5rem;
    line-height: 1.4rem;
  }
`;

const ExampleOfTreatment = styled.div`
  border: 2px solid aqua;
  width: 90%;
  p {
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.5rem;
    line-height: 1.4rem;
  }
`;
