import React from "react";
import styled from "styled-components";

export default function TreatmentDetailsContainer() {
  return (
    <MainContainer>
      <TreatmentDetails>
        <TreatmentHeadingContainers>
          <p key={index}>{item.NgoHeading}</p>
        </TreatmentHeadingContainers>
      </TreatmentDetails>
    </MainContainer>
  );
}

const MainContainer = styled.div`
  width: 90%;
  position: relative;
  left: 80px;
  top: 40px;
  text-align: center;
  z-index: -1;
`;

const TreatmentDetails = styled.div`
  box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  border-radius: 5px;
  background-color: white;
`;

const TreatmentHeadingContainers = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 3%;

  p {
    text-align: center;
    font-family: "Bree Serif", serif;
    font-size: 1.5rem;
    color: white;
    border-radius: 5px;
    font-weight: bold;
    display: inline-flex;
    padding: 4px;
    margin-top: 30px;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
    border-radius: 5px;
    background-color: white;
    color: #090b13;
    /* box-shadow: 0px 0px 15px 4px #090b13; */
    /* background-color: #090b13; */
  }
`;
