import React from "react";
import styled from "styled-components";

export default function DonationForm() {
  return (
    <MainContainer>
      <LeftArrowContainer>
        <img src="/DonateImages/left-arrow.png" alt="MyHospitalWebsite" />
      </LeftArrowContainer>
      <BloodDonationContainer>
        <ImgAndHeadingAndParagraphContainer>
          <ImgContainer>
            <img
              src="/DonateImages/BloodDontaionFormImage2.png"
              alt="MyHospitalWebApp"
            />
          </ImgContainer>
          <HeadingAndDetailsContainer>
            <MainHeadingContainer>Blood Donation Form</MainHeadingContainer>
            <MainDetailsContainer>
              Lorem ipsum, dolor sit amet consectetur adipisicing elit. Porro
              quae pariatur modi tempore, enim, facilis nemo reprehenderit
              fugiat quo deleniti itaque inventore animi rem molestiae,
              voluptate in! Molestias nobis quaerat nemo placeat numquam eaque?
            </MainDetailsContainer>
          </HeadingAndDetailsContainer>
        </ImgAndHeadingAndParagraphContainer>
        <FormDetailsContainer>
          <form>
            <NameContainer>
              <InnerNameContainer>
                <input type="text" placeholder="First name of donar" />
              </InnerNameContainer>
              <InnerNameContainer>
                <input type="text" placeholder="Last name of donar" />
              </InnerNameContainer>
            </NameContainer>
            <InnerNameContainer>
              <input type="email" placeholder="Email of donar " />
            </InnerNameContainer>
            <PhoneNumberContainer>
              <CountryCodeContainer>
                <input type="text" placeholder="Country code" />
              </CountryCodeContainer>
              <PhoneContainer>
                <input type="text" placeholder="Phone number " />
              </PhoneContainer>
            </PhoneNumberContainer>
            <InnerNameContainer>
              <input type="text" placeholder="Donar Address" />
            </InnerNameContainer>
            <InnerNameContainer>
              <input type="text" placeholder=" Occupation" />
            </InnerNameContainer>

            <QuestionLabel>
              <p>What is Your Blood Type ?</p>
            </QuestionLabel>
            <BloodGroupContainer>
              <PositiveBloodContainer>
                <InnerContainer>
                  <input type="radio" name="YouBloodGroup" value="ORh+" />
                  <label>O Rh+</label>
                </InnerContainer>
                <InnerContainer>
                  <input type="radio" name="YouBloodGroup" value="Ah+" />
                  <label>A Rh+</label>
                </InnerContainer>
                <InnerContainer>
                  <input type="radio" name="YouBloodGroup" value="Bh+" />
                  <label>B Rh+</label>
                </InnerContainer>
                <InnerContainer>
                  <input type="radio" name="YouBloodGroup" value="ABh+" />
                  <label>AB Rh+</label>
                </InnerContainer>
              </PositiveBloodContainer>
              <NegativeBloodContainer>
                <InnerContainer>
                  <input type="radio" name="YouBloodGroup" value="ORh-" />
                  <label>O Rh-</label>
                </InnerContainer>
                <InnerContainer>
                  <input type="radio" name="YouBloodGroup" value="Ah-" />
                  <label>A Rh-</label>
                </InnerContainer>
                <InnerContainer>
                  <input type="radio" name="YouBloodGroup" value="Bh-" />
                  <label>B Rh-</label>
                </InnerContainer>
                <InnerContainer>
                  <input type="radio" name="YouBloodGroup" value="ABh-" />
                  <label>AB Rh-</label>
                </InnerContainer>
              </NegativeBloodContainer>
            </BloodGroupContainer>

            {/* question container 3 */}

            <QuestionLabel>
              <p>Have you done a blood donation before?</p>
            </QuestionLabel>
            <ChoiceConatainer>
              <ChoiceInnerConatainer>
                <input type="radio" name="DonateBefore" value="yes" />
                <label>yes</label>
              </ChoiceInnerConatainer>
              <ChoiceInnerConatainer>
                <input type="radio" name="DonateBefore" value="no" />
                <label>No</label>
              </ChoiceInnerConatainer>
            </ChoiceConatainer>

            {/* question container 4  */}

            <QuestionLabel>
              <p>Have you had any disease?</p>
            </QuestionLabel>
            <ChoiceConatainer>
              <ChoiceInnerConatainer>
                <input type="radio" name="DonateBefore" value="yes" />
                <label>yes</label>
              </ChoiceInnerConatainer>
              <ChoiceInnerConatainer>
                <input type="radio" name="DonateBefore" value="no" />
                <label>No</label>
              </ChoiceInnerConatainer>
            </ChoiceConatainer>

            {/* question container 5  */}

            <QuestionLabel>
              <p>Do you have any allergies?</p>
            </QuestionLabel>
            <ChoiceConatainer>
              <ChoiceInnerConatainer>
                <input type="radio" name="DonateBefore" value="yes" />
                <label>yes</label>
              </ChoiceInnerConatainer>
              <ChoiceInnerConatainer>
                <input type="radio" name="DonateBefore" value="no" />
                <label>No</label>
              </ChoiceInnerConatainer>
            </ChoiceConatainer>

            {/* Terms and Conditions  */}

            <ChoiceConatainer>
              <ChoiceInnerConatainer>
                <input type="radio" name="DonateBefore" value="yes" />
                <label>
                  I agree
                  <a
                    href="https://www.google.com/"
                    style={{ textDecoration: "underline" }}
                  >
                    terms and condition
                  </a>
                </label>
              </ChoiceInnerConatainer>
            </ChoiceConatainer>
          </form>
        </FormDetailsContainer>
      </BloodDonationContainer>
      <RightArrowContainer>
        <img src="/DonateImages/Right-Arrow.png" alt="MyHospitalWebsite" />
      </RightArrowContainer>
    </MainContainer>
  );
}

