import React from "react";
import styled from "styled-components";

export default function SlideComponent() {
  return (
    <SideContainer>
      <HoveredContainer>
        <div>
          <img src="/Images/facebook.png" alt="MyHospitalWebApp" />
        </div>
      </HoveredContainer>
      <HoveredContainer>
        <div>
          <img src="/Images/facebook.png" alt="MyHospitalWebApp" />
        </div>
      </HoveredContainer>
      <HoveredContainer>
        <div>
          <img src="/Images/facebook.png" alt="MyHospitalWebApp" />
        </div>
      </HoveredContainer>
    </SideContainer>
  );
}

const SideContainer = styled.div`
  position: fixed;
  top: 20%;
  display: flex;
  flex-direction: column;
  width: 4%;
  margin-bottom: 20%;
  background-color: #090b13;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const HoveredContainer = styled.div`

  height: 50px;
  width: 50px;

  div {
    width: 100%;
    height: 100%;
    display: flex;

    img {
      width: 100%;
      height: 100%;
      filter: invert(100);
      position: sticky;
      left: 0px;
      top: 0px;
      z-index: 3;
    }
    &::after {
      content: "pankaj";
      color: white;
      background-color: white;
      height: 50px;
      width: auto;
      transform-origin: left center;
      width: auto;
      transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
      opacity: 0;
      visibility: hidden;
      transform: scaleX(0);
    }
  }
  &:hover {
    div:after {
      transform: scaleX(1);
      visibility: visible;
      opacity: 1;
    }
  }
`;

