import React from "react";
import styled from "styled-components";

export default function SlideComponent() {
  return (
    <SideContainer>
      <button>
         <img src="/Images/facebook.png" alt="MyHospitalWeb" />
      </button>
      <button>
        <span>Option 2</span>
      </button>
      <button>
        <span>Option 3</span>
      </button>
      <button>
        <span>Option 4</span>
      </button>
      <button>
        <span>Option 5</span>
      </button>
    </SideContainer>
  );
}

const SideContainer = styled.div`
  border: 2px solid green;
  position: fixed;
  top: 20%;
  display: flex;
  flex-direction: column;
  width: 15%;
  margin-bottom: 20%;
  button {
    width: 60%;
    margin: 0px 5px;

    border-radius: 5px;
    span {
      font-size: 1.3rem;
      &::after {
        content: "Pankaj";
        background-color: crimson;
        border-radius: 5px;
        height: auto;
        transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
        position: absolute;
        opacity: 0;
        visibility: hidden;
        transform: scaleX(0);
        transform-origin: left center;
      }
    }
    &:hover {
      span:after {
        transform: scaleX(1);
        visibility: visible;
        opacity: 1;
        cursor: pointer;
      }
    }
  }
`;
