    /* css for blood group main container  */

    /* .DonarBloodGroupContainer {
      border: 2px solid navy;
    } */

    .Title_Of_Container {
      margin-top: 1%;
      display: flex;
      align-items: flex-start;

      padding: 1.5px;
      p {
        display: inline-block;
        margin: 2px;
        font-size: 1.2rem;
        color: green;
        font-family: "Open Sans", sans-serif;
        padding: 4px;
