import React from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import styled from "styled-components";
import Slider from "react-slick";

export default function ImageSlider() {
  let settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
  };
  return (
    <div>
      <Carousel {...settings}>
        <wrap>
          <a>
            <img src="/Images/bacgroundImg(1).jpg" alt="MyHospitalWebsite" />
          </a>
        </wrap>
        <wrap>
          <a>
            <img src="/Images/bacgroundImg(2).jpg" alt="MyHospitalWebsite" />
          </a>
        </wrap>
        <wrap>
          <a>
            <img src="/Images/bacgroundImg(3).jpg" alt="MyHospitalWebsite" />
          </a>
        </wrap>
        <wrap>
          <a>
            <img src="/Images/bacgroundImg(4).jpg" alt="MyHospitalWebsite" />
          </a>
        </wrap>
        <wrap>
          <a>
            <img src="/Images/bacgroundImg(5).jpg" alt="MyHospitalWebsite" />
          </a>
        </wrap>
      </Carousel>
    </div>
  );
}

const Carousel = styled(Slider)`
  position: relative;
  top: 100px;
  width: 95%;
  height: 550px;
  max-width: 1200px; //check
  margin: 0 auto;
  padding: 3rem 0;
  text-align: center;

  wrap {
    width: 100%;
    height: 100%;
  }

  a {
    cursor: pointer;
  }
  img {
    width: 100%;
    height: 100%;
  }
`;

