import React from "react";
import styled from "styled-components";

import ImgSlider from "../../mainslider/ImgSlider";

export default function Event() {
  let slidersetting = {
    slidetoshow: 3,
  };
  let data = [
    {
      img: "/Images/bacgroundImg(1).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/Images/bacgroundImg(2).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/Images/bacgroundImg(3).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/Images/bacgroundImg(4).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/Images/bacgroundImg(5).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
  ];

  let details = [
    {
      EventHeading: "Lorem, ipsum dolor.",
      EventInformation:
        "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Saepe fugiat nulla accusantium fugit aspernatur blanditiis quibusdam enim ratione deserunt sint magni totam, eveniet, deleniti esse veniam voluptatem quaerat dicta eos est aliquam!",
    },
    {
      EventHeading: "Lorem, ipsum dolor.",
      EventInformation:
        "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Saepe fugiat nulla accusantium fugit aspernatur blanditiis quibusdam enim ratione deserunt sint magni totam, eveniet, deleniti esse veniam voluptatem quaerat dicta eos est aliquam!",
    },
    {
      EventHeading: "Lorem, ipsum dolor.",
      EventInformation:
        "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Saepe fugiat nulla accusantium fugit aspernatur blanditiis quibusdam enim ratione deserunt sint magni totam, eveniet, deleniti esse veniam voluptatem quaerat dicta eos est aliquam!",
    },
  ];

  function showDetails() {
    alert("Thanks For Showing The Interest ");
  }

  return (
    <div>
      <ImgDivContainer>
        <ImgSlider data={data} slidersetting={slidersetting} />
        <EventContainer>
          {details.map((item, index) => {
            return (
              <EventsContainers>
                <h2>{item.EventHeading}</h2>
                <p>{item.EventInformation}</p>
                <LikeDisLikeButtonContainer>
                  <LikeButton onClick={showDetails}>
                    <img
                      src="/EventImages/likeunColored.png"
                      alt="MyHospitalWebApp"
                    />
                  </LikeButton>
                  <DislikeButton onClick={showDetails}>
                    <img
                      src="/EventImages/dislikeUncolored.png"
                      alt="MyHospitalWebApp"
                    />
                  </DislikeButton>
                  <p>checkCount</p>
                </LikeDisLikeButtonContainer>

                <button onClick={showDetails} className="ViewDetailsButton">
                  View Details{" "}
                </button>
              </EventsContainers>
            );
          })}
        </EventContainer>
      </ImgDivContainer>
    </div>
  );
}

const ImgDivContainer = styled.div`
  position: relative;
  top: 60px;
`;

const EventContainer = styled.div`
  position: relative;
  top: 50px;
  box-sizing: border-box;
  display: flex;
`;

const EventsContainers = styled.div`
  margin: 2%;
  border-radius: 50%;
  width: 350px;
  height: 350px;
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease-in-out;
  box-shadow: rgb(0 0 0 / 69%) 0px 26px 30px -10px,
    rgb(0 0 0 / 73%) 0px 16px 10px -10px;
  background-color: white;

  h2 {
    text-align: center;
    padding: 6px;
    margin: 2%;
  }
  p {
    /* text-align: center;
    padding: 6px;
    margin: 2%; */

    text-align: center;
    font-family: "Bree Serif", serif;
    font-size: 1.2rem;

    font-weight: bold;

    padding: 10px;

    word-spacing: 0.5rem;
    line-height: 1.4rem;
    margin: 10px;
  }

  .ViewDetailsButton {
    color: #090b13;
    font-family: "Roboto", sans-serif;
    font-size: 1.2rem;
    padding: 3px;
    margin: 10px;
    border-radius: 10px;
    cursor: pointer;
    transition: all 0.3s ease-in-out;
    border: 2px solid #090b13;
    &:hover {
      transform: scale(1.06);
      background-color: #090b13;
      color: white;
    }
  }

  &:hover {
    transform: scale(1.06);
    cursor: pointer;
  }
`;

// buttons css

const LikeDisLikeButtonContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

const LikeButton = styled.button`
  width: 10%;
  margin: 8px;
  img {
    width: 100%;
    height: 100%;
  }
`;

const DislikeButton = styled.button`
  width: 10%;
  margin: 8px;
  img {
    width: 100%;
    height: 100%;
  }
`;
