import React from "react";
import styled from "styled-components";
import InnerImgSlider from "../innersmallslider/InnerImgSlider";

export default function Details() {
  return (
    <MainContainer>
      <HeadingContainers>
        <p>"Promote Health, Keep The World Safe, Serve The Vulnerable"</p>
      </HeadingContainers>
      <HeadingContainers>
        <p>Our Work Toward Remote Area</p>
      </HeadingContainers>
      <HeadingContainers>
        <p>We For You</p>
      </HeadingContainers>
      <WorkDetails>
        <p>
          We for You (WFY) was founded in 2007 by our doctors, medical students
          and like-minded people with a vision of “health for all”. Currently,
          WFY is working on various projects in 17 different states across India
          involving healthcare professionals, disaster management practitioners,
          social workers, etc. It reaches out to segments affected by extreme
          poverty, disasters, neglect and years of injustice.
        </p>
      </WorkDetails>
      <InnerImgSlider />

      <WorkDetails>
        <p>
          Our major focus is to address the healthcare and nutritional needs of
          the affected population. We also work to improve sexual and
          reproductive health, maternal, infant and child health in emergencies
          and have vast experience working in disaster-prone areas. WFY has
          received several awards like the SAARC Award (2010), The British
          Medical Journal Group Award (2009), Golden Ruby Award (2015) and
          recognition from state governments for their outstanding contribution
          to the humanitarian field.
        </p>
      </WorkDetails>

      <HeadingContainers>
        <p>Rural Heath Treatment Foundation</p>
      </HeadingContainers>
      <WorkDetails>
        <p>
          Rural Health Treatment Foundation (RHTF) has been working for
          healthcare since 2009 and provides high quality and affordable primary
          medical care to low-income and underprivileged groups in West Bengal.
          Their aim is to make healthcare accessible to the poorest and the most
          deprived sections. RHTF has 17 centres operating across the state
          which include 12 centres in remote rural areas. Their goal is to
          continue improving the health and lives of the underserved who face
          social and financial challenges.
        </p>
      </WorkDetails>
      <InnerImgSlider />
      <WorkDetails>
        <p>
          Their healthcare centres provide patients with affordable medical
          consultations along with a week's supply of free medication. To date,
          over 23 lakh patients have been treated at the RHTF centres.
        </p>
      </WorkDetails>

      <HeadingContainers>
        <p>Foundation for Mother and Child Health</p>
      </HeadingContainers>
    </MainContainer>
  );
}

const MainContainer = styled.div`
  width: 90%;
  position: relative;
  left: 80px;
  top: 40px;
`;

const HeadingContainers = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 3%;
  p {
    text-align: center;
    font-family: "Open Sans", sans-serif;
    font-size: 1.5rem;
    color: #090b13;
    font-weight: bold;
    display: inline-flex;
    padding: 4px;
    box-shadow: 0px 0px 15px 4px #090b13;
  }
`;

const WorkDetails = styled.div`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  margin: 3%;
  p {
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
  }
`;
