import React from "react";
import styled from "styled-components";

export default function SlideComponent() {
  return (
    <SideContainer>
      <HoveredContainer>
        <div>
          <img src="/Images/facebook.png" alt="MyHospitalWebApp" />
          <a href="@https://www.facebook.com/pankajsingh.raikwal.3/">
            Connnet On Facebook
          </a>
        </div>
      </HoveredContainer>
      <HoveredContainer>
        <div>
          <img src="/Images/instagram.png" alt="MyHospitalWebApp" />
          <a href="@https://www.instagram.com/">Connnet On Instagram</a>
        </div>
      </HoveredContainer>
      <HoveredContainer>
        <div>
          <img src="/Images/linkedin.png" alt="MyHospitalWebApp" />
          <a href="@https://www.linkedin.com/in/pankaj-singh-243155256/">
            Connnet On Linkedin
          </a>
        </div>
      </HoveredContainer>
      <HoveredContainer>
        <div>
          <img src="/Images/twitter.png" alt="MyHospitalWebApp" />
          <a href="@https://twitter.com/i/flow/login?input_flow_data=%7B%22requested_variant%22%3A%22eyJsYW5nIjoiZW4ifQ%3D%3D%22%7D">
            Connnet On Twitter
          </a>
        </div>
      </HoveredContainer>
      <HoveredContainer>
        <div>
          <img src="/Images/email.png" alt="MyHospitalWebApp" />
          <a href="https://mail.google.com/mail/u/0/#inbox">Connnet On Email</a>
        </div>
      </HoveredContainer>
    </SideContainer>
  );
}

const SideContainer = styled.div`
  position: fixed;
  top: 20%;
  display: flex;
  flex-direction: column;
  width: 4%;
  margin-bottom: 20%;
  background-color: white;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const HoveredContainer = styled.div`
  height: 50px;
  width: 50px;
  padding: 2px;
  div {
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    img {
      width: 100%;
      height: 100%;
      /* filter: invert(100); */
      cursor: pointer;
      position: sticky;
      left: 0px;
      top: 0px;
      z-index: 3;
    }
    a {
      border: 2px solid green;
      text-decoration: none;
      color: black;
      position: absolute;
      left: 47px;
      width: 100px;
      text-align: center;
      background-color: white;
      z-index: 3;
    }

    &::after {
      content: "";
      color: black;
      display: flex;
      align-items: center;
      background-color: white;
      height: 54px;
      width: auto;
      transform-origin: left center;
      width: auto;
      transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
      opacity: 0;
      visibility: hidden;
      transform: scaleX(0);
    }
  }
  &:hover {
    div:after {
      transform: scaleX(5);
      visibility: visible;
      opacity: 1;
      cursor: pointer;
    }
  }
`;
