import React from "react";
import styled from "styled-components";

export default function DonationForm() {
  return (
    <MainContainer>
      <LeftArrowContainer>
        <img src="/DonateImages/left-arrow.png" alt="MyHospitalWebsite" />
      </LeftArrowContainer>
      <BloodDonationContainer>
        <ImgAndHeadingAndParagraphContainer>
          <ImgContainer>
            <img
              src="/DonateImages/BloodDontaionFormImage2.png"
              alt="MyHospitalWebApp"
            />
          </ImgContainer>
          <HeadingAndDetailsContainer>
            <MainHeadingContainer>Blood Donation Form</MainHeadingContainer>
            <MainDetailsContainer>
              Lorem ipsum, dolor sit amet consectetur adipisicing elit. Porro
              quae pariatur modi tempore, enim, facilis nemo reprehenderit
              fugiat quo deleniti itaque inventore animi rem molestiae,
              voluptate in! Molestias nobis quaerat nemo placeat numquam eaque?
            </MainDetailsContainer>
          </HeadingAndDetailsContainer>
        </ImgAndHeadingAndParagraphContainer>
        <FormDetailsContainer>
          <form>
            <NameContainer>
              <input type="text" placeholder="first name of donar" />
              <input type="text" placeholder="last name of donar" />
            </NameContainer>
            <Question1Label>
              <p>What is Your Blood Type ?</p>
            </Question1Label>
            <BloodGroupContainer>
              <PositiveBloodContainer>
                <InnerContainer>
                  <label>O Rh+</label>
                  <input type="radio" name="YouBloodGroup" value="ORh+" />
                </InnerContainer>
                <InnerContainer>
                  <label>A Rh+</label>
                  <input type="radio" name="YouBloodGroup" value="Ah+" />
                </InnerContainer>
                <InnerContainer>
                  <label>B Rh+</label>
                  <input type="radio" name="YouBloodGroup" value="Bh+" />
                </InnerContainer>
                <InnerContainer>
                  <label>AB Rh+</label>
                  <input type="radio" name="YouBloodGroup" value="ABh+" />
                </InnerContainer>
              </PositiveBloodContainer>
              <NegativeBloodContainer>
                <InnerContainer>
                  <label>O Rh-</label>
                  <input type="radio" name="YouBloodGroup" value="ORh-" />
                </InnerContainer>
                <InnerContainer>
                  <label>A Rh-</label>
                  <input type="radio" name="YouBloodGroup" value="Ah-" />
                </InnerContainer>
                <InnerContainer>
                  <label>B Rh-</label>
                  <input type="radio" name="YouBloodGroup" value="Bh-" />
                </InnerContainer>
                <InnerContainer>
                  <label>AB Rh-</label>
                  <input type="radio" name="YouBloodGroup" value="ABh-" />
                </InnerContainer>
              </NegativeBloodContainer>
            </BloodGroupContainer>
          </form>
        </FormDetailsContainer>
      </BloodDonationContainer>
      <RightArrowContainer>
        <img src="/DonateImages/Right-Arrow.png" alt="MyHospitalWebsite" />
      </RightArrowContainer>
    </MainContainer>
  );
}

// MainContainer For Dontains form

const MainContainer = styled.div`
  border: 2px solid purple;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;

// left arrow container

const LeftArrowContainer = styled.div`
  border: 2px solid pink;
  width: 5%;
  img {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    transition: all 0.3s ease-in-out;
    &:hover {
      cursor: pointer;
      transform: scale(1.08);
    }
  }
`;

// BloodDonation Form Container

const BloodDonationContainer = styled.div`
  border: 2px solid pink;
  box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  background-color: rgb(255, 255, 255);
  color: #090b13;
  width: 90%;
`;

const ImgAndHeadingAndParagraphContainer = styled.div`
  border: 2px solid grey;
  display: flex;
  flex-direction: row;
  padding: 4px;
  margin: 3px;
`;
const HeadingAndDetailsContainer = styled.div`
  border: 2px solid purple;
  box-sizing: border-box;
  width: 100%;
  padding: 3px;
  margin: 1px;
`;

const MainHeadingContainer = styled.p`
  text-align: center;
  font-family: "Bree Serif", serif;
  font-size: 2rem;
  color: white;
  font-weight: bold;
  display: inline-flex;
  padding: 10px;
  color: #090b13;
  border-radius: 5px;
  word-spacing: 0.5rem;
  line-height: 1.4rem;
`;

const MainDetailsContainer = styled.p`
  text-align: center;
  color: #090b13;
  font-family: "Roboto", sans-serif;
  word-spacing: 0.5rem;
  line-height: 1.4rem;
  box-sizing: border-box;
  margin-top: 15px;
`;

const ImgContainer = styled.div`
  border: 2px solid pink;
  width: 18%;
  padding: 3px;
  margin: 1px;
  img {
    width: 100%;
    height: 100%;
    border: 2px solid purple;
  }
`;

// Form Details Container

const FormDetailsContainer = styled.div`
  border: 2px solid green;
  margin-top: 3%;
`;

const NameContainer = styled.div`
  border: 2px solid pink;
  input {
    border: 2px solid purple;
    margin: 3px;
  }
`;

const Question1Label = styled.label`
  border: 2px solid greenyellow;
  display: flex;
  justify-content: flex-start;
  margin: 3px;
  p {
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    box-sizing: border-box;
    margin-top: 15px;
  }
`;

const BloodGroupContainer = styled.div`
  border: 2px solid purple;
  display: flex;
  align-items: flex-start;
  justify-content: center;
`;

const PositiveBloodContainer = styled.div`
  border: 2px solid pink;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
`;

const NegativeBloodContainer = styled.div`
  border: 2px solid yellowgreen;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
`;

const InnerContainer = styled.div`
  border: 2px solid crimson;
  display: flex;
  align-items: center;
  width: 100%;
  label {
    text-align: left;
    padding: 4px;
    margin: 3px;
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    box-sizing: border-box;
    margin-top: 15px;
  }
  input {
    padding: 2px;
    margin: 3px;
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    box-sizing: border-box;
    margin-top: 15px;
  }
`;

// Right Arrow Container

const RightArrowContainer = styled.div`
  border: 2px solid pink;
  width: 5%;
  img {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    transition: all 0.3s ease-in-out;
    &:hover {
      cursor: pointer;
      transform: scale(1.08);
    }
  }
`;
