import React from "react";
import styled from "styled-components";

export default function SlideComponent() {
  return (
    <SideContainer>
      <HoveredContainer>
        <div>
          <img src="/Images/facebook.png" alt="MyHospitalWebApp" />
        </div>
      </HoveredContainer>
    </SideContainer>
  );
}

const SideContainer = styled.div`
  border: 2px solid green;
  position: fixed;
  top: 20%;
  display: flex;
  flex-direction: column;
  width: 15%;
  margin-bottom: 20%;
  background-color: #090b13;
`;

const HoveredContainer = styled.div`
  border: 2px solid white;
  width: 32%;
  div {
    border: 2px solid crimson;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    img {
      width: 100%;
      height: 100%;
      filter: invert(100);
      position: relative;
      left: 20px;
      z-index: 2;
    }
    &::after {
      content: "pankaj";
      color: white;
      background-color: white;
      height: 50px;
      width: auto;
      transform-origin: left center;
      width: auto;
      transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
      opacity: 0;
    visibility: hidden;
    transform: scaleX(0);
    }
  }
  &:hover {
    div:after {
      transform: scaleX(7);
      visibility: visible;
      opacity: 1;
      z-index: -1;
    }
  }
`;

