import React from "react";
import styled from "styled-components";

import ImgSlider from "../../mainslider/ImgSlider";

export default function Event() {
  let slidersetting = {
    slidetoshow: 3,
  };
  let data = [
    {
      img: "/Images/bacgroundImg(1).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/Images/bacgroundImg(2).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/Images/bacgroundImg(3).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/Images/bacgroundImg(4).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
    {
      img: "/Images/bacgroundImg(5).jpg",
      discription: "Description",
      disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
    },
  ];

  let details = [
    {
      EventHeading: "Lorem, ipsum dolor.",
      EventInformation:
        "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Saepe fugiat nulla accusantium fugit aspernatur blanditiis quibusdam enim ratione deserunt sint magni totam, eveniet, deleniti esse veniam voluptatem quaerat dicta eos est aliquam!",
    },
    {
      EventHeading: "Lorem, ipsum dolor.",
      EventInformation:
        "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Saepe fugiat nulla accusantium fugit aspernatur blanditiis quibusdam enim ratione deserunt sint magni totam, eveniet, deleniti esse veniam voluptatem quaerat dicta eos est aliquam!",
    },
    {
      EventHeading: "Lorem, ipsum dolor.",
      EventInformation:
        "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Saepe fugiat nulla accusantium fugit aspernatur blanditiis quibusdam enim ratione deserunt sint magni totam, eveniet, deleniti esse veniam voluptatem quaerat dicta eos est aliquam!",
    },
  ];

  function showDetails() {
    alert("Thanks For Showing The Interest ");
  }

  return (
    <div>
      <ImgDivContainer>
        <ImgSlider data={data} slidersetting={slidersetting} />
        <EventContainer>
          {details.map((item, index) => {
            return (
              <EventsContainers>
                <h2>{item.EventHeading}</h2>
                <p>{item.EventInformation}</p>
                <LikeDisLikeButtonContainer>
                  <LikeButton>Like</LikeButton>
                  <DislikeButton>DisLike</DislikeButton>
                </LikeDisLikeButtonContainer>

                <button onClick={showDetails}>View Details </button>
              </EventsContainers>
            );
          })}
        </EventContainer>
      </ImgDivContainer>
    </div>
  );
}

const ImgDivContainer = styled.div`
  position: relative;
  top: 60px;
`;

const EventContainer = styled.div`
  border: 2px solid green;
  position: relative;
  top: 50px;
  box-sizing: border-box;
  display: flex;
`;

const EventsContainers = styled.div`
  margin: 2%;
  border-radius: 50%;
  width: 350px;
  height: 350px;
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease-in-out;
  box-shadow: rgb(0 0 0 / 69%) 0px 26px 30px -10px,
    rgb(0 0 0 / 73%) 0px 16px 10px -10px;
  background-color: white;
  border: 2px solid grey;
  h2 {
    text-align: center;
    padding: 6px;
  }
  p {
    text-align: center;
    padding: 6px;
  }

  &:hover {
    transform: scale(1.06);
    cursor: pointer;
  }
`;

// buttons css

const LikeDisLikeButtonContainer = styled.div`
  border: 2px solid green;
`;

const LikeButton = styled.button`
  border: 2px solid pink;
`;

const DislikeButton = styled.button`
  border: 2px solid pink;
`;
