import React from "react";
import styled from "styled-components";

export default function DonationForm() {
  return (
    <MainContainer>
      <LeftArrowContainer>
        <img src="/DonateImages/left-arrow.png" alt="MyHospitalWebsite" />
      </LeftArrowContainer>

      {/* Form 1 Container For Swapping  */}

      <DonationOfBloodForm>
        <BloodDonationContainer>
          <ImgAndHeadingAndParagraphContainer>
            <ImgContainer>
              <img
                src="/DonateImages/BloodDontaionFormImage2.png"
                alt="MyHospitalWebApp"
              />
            </ImgContainer>
            <HeadingAndDetailsContainer>
              <MainHeadingContainer>Blood Donation Form</MainHeadingContainer>
              <MainDetailsContainer>
                It is said that blood is one of the most priceless gifts one can
                give to another. Blood is essential for a person to stay alive.
                Many times due to accidents or any other serious ailments, a
                person might require blood. And in those times, people who step
                up to donate their blood are real-life superheroes
              </MainDetailsContainer>
            </HeadingAndDetailsContainer>
          </ImgAndHeadingAndParagraphContainer>
          <FormDetailsContainer>
            <form>
              <NameContainer>
                <InnerNameContainer>
                  <input type="text" placeholder="First name of donar" />
                </InnerNameContainer>
                <InnerNameContainer>
                  <input type="text" placeholder="Last name of donar" />
                </InnerNameContainer>
              </NameContainer>
              <NameContainer>
                <InnerNameContainer>
                  <input type="email" placeholder="Email of donar " />
                </InnerNameContainer>
              </NameContainer>

              <PhoneNumberContainer>
                <CountryCodeContainer>
                  <input type="text" placeholder="Country code" />
                </CountryCodeContainer>
                <PhoneContainer>
                  <input type="text" placeholder="Phone number " />
                </PhoneContainer>
              </PhoneNumberContainer>

              <NameContainer>
                <InnerNameContainer>
                  <input type="text" placeholder="Donar Address" />
                </InnerNameContainer>
              </NameContainer>
              <NameContainer>
                <InnerNameContainer>
                  <input type="text" placeholder=" Occupation" />
                </InnerNameContainer>
              </NameContainer>

              <QuestionLabel>
                <p>What is Your Blood Type ?</p>
              </QuestionLabel>
              <BloodGroupContainer>
                <PositiveBloodContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="ORh+" />
                    <label>O Rh+</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="Ah+" />
                    <label>A Rh+</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="Bh+" />
                    <label>B Rh+</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="ABh+" />
                    <label>AB Rh+</label>
                  </InnerContainer>
                </PositiveBloodContainer>
                <NegativeBloodContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="ORh-" />
                    <label>O Rh-</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="Ah-" />
                    <label>A Rh-</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="Bh-" />
                    <label>B Rh-</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="ABh-" />
                    <label>AB Rh-</label>
                  </InnerContainer>
                </NegativeBloodContainer>
              </BloodGroupContainer>

              {/* question container 3 */}

              <QuestionLabel>
                <p>Have you done a blood donation before?</p>
              </QuestionLabel>
              <ChoiceConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="yes" />
                  <label>yes</label>
                </ChoiceInnerConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="no" />
                  <label>No</label>
                </ChoiceInnerConatainer>
              </ChoiceConatainer>

              {/* question container 4  */}

              <QuestionLabel>
                <p>Have you had any disease?</p>
              </QuestionLabel>
              <ChoiceConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="yes" />
                  <label>yes</label>
                </ChoiceInnerConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="no" />
                  <label>No</label>
                </ChoiceInnerConatainer>
              </ChoiceConatainer>

              {/* question container 5  */}

              <QuestionLabel>
                <p>Do you have any allergies?</p>
              </QuestionLabel>
              <ChoiceConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="yes" />
                  <label>yes</label>
                </ChoiceInnerConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="no" />
                  <label>No</label>
                </ChoiceInnerConatainer>
              </ChoiceConatainer>

              {/* Terms and Conditions  */}

              <TermsAndConditionConatainer>
                <TermsAndConditionInnerConatainer>
                  <input type="checkbox" name="DonateBefore" value="yes" />
                  <label>
                    I agree &nbsp;
                    <a
                      href="https://www.google.com/"
                      style={{ textDecoration: "underline" }}
                    >
                      terms&condition
                    </a>
                  </label>
                </TermsAndConditionInnerConatainer>
              </TermsAndConditionConatainer>
            </form>
          </FormDetailsContainer>
        </BloodDonationContainer>
      </DonationOfBloodForm>

      {/* Money Donation Form container  */}

      <DonationOfMoneyForm>
        <BloodDonationContainer>
          <ImgAndHeadingAndParagraphContainer>
            <ImgContainer>
              <img
                src="/DonateImages/BloodDontaionFormImage2.png"
                alt="MyHospitalWebApp"
              />
            </ImgContainer>
            <HeadingAndDetailsContainer>
              <MainHeadingContainer>Blood Donation Form</MainHeadingContainer>
              <MainDetailsContainer>
                It is said that blood is one of the most priceless gifts one can
                give to another. Blood is essential for a person to stay alive.
                Many times due to accidents or any other serious ailments, a
                person might require blood. And in those times, people who step
                up to donate their blood are real-life superheroes
              </MainDetailsContainer>
            </HeadingAndDetailsContainer>
          </ImgAndHeadingAndParagraphContainer>
          <FormDetailsContainer>
            <form>
              <NameContainer>
                <InnerNameContainer>
                  <input type="text" placeholder="First name of donar" />
                </InnerNameContainer>
                <InnerNameContainer>
                  <input type="text" placeholder="Last name of donar" />
                </InnerNameContainer>
              </NameContainer>
              <NameContainer>
                <InnerNameContainer>
                  <input type="email" placeholder="Email of donar " />
                </InnerNameContainer>
              </NameContainer>

              <PhoneNumberContainer>
                <CountryCodeContainer>
                  <input type="text" placeholder="Country code" />
                </CountryCodeContainer>
                <PhoneContainer>
                  <input type="text" placeholder="Phone number " />
                </PhoneContainer>
              </PhoneNumberContainer>

              <NameContainer>
                <InnerNameContainer>
                  <input type="text" placeholder="Donar Address" />
                </InnerNameContainer>
              </NameContainer>
              <NameContainer>
                <InnerNameContainer>
                  <input type="text" placeholder=" Occupation" />
                </InnerNameContainer>
              </NameContainer>

              <QuestionLabel>
                <p>What is Your Blood Type ?</p>
              </QuestionLabel>
              <BloodGroupContainer>
                <PositiveBloodContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="ORh+" />
                    <label>O Rh+</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="Ah+" />
                    <label>A Rh+</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="Bh+" />
                    <label>B Rh+</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="ABh+" />
                    <label>AB Rh+</label>
                  </InnerContainer>
                </PositiveBloodContainer>
                <NegativeBloodContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="ORh-" />
                    <label>O Rh-</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="Ah-" />
                    <label>A Rh-</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="Bh-" />
                    <label>B Rh-</label>
                  </InnerContainer>
                  <InnerContainer>
                    <input type="radio" name="YouBloodGroup" value="ABh-" />
                    <label>AB Rh-</label>
                  </InnerContainer>
                </NegativeBloodContainer>
              </BloodGroupContainer>

              {/* question container 3 */}

              <QuestionLabel>
                <p>Have you done a blood donation before?</p>
              </QuestionLabel>
              <ChoiceConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="yes" />
                  <label>yes</label>
                </ChoiceInnerConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="no" />
                  <label>No</label>
                </ChoiceInnerConatainer>
              </ChoiceConatainer>

              {/* question container 4  */}

              <QuestionLabel>
                <p>Have you had any disease?</p>
              </QuestionLabel>
              <ChoiceConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="yes" />
                  <label>yes</label>
                </ChoiceInnerConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="no" />
                  <label>No</label>
                </ChoiceInnerConatainer>
              </ChoiceConatainer>

              {/* question container 5  */}

              <QuestionLabel>
                <p>Do you have any allergies?</p>
              </QuestionLabel>
              <ChoiceConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="yes" />
                  <label>yes</label>
                </ChoiceInnerConatainer>
                <ChoiceInnerConatainer>
                  <input type="radio" name="DonateBefore" value="no" />
                  <label>No</label>
                </ChoiceInnerConatainer>
              </ChoiceConatainer>

              {/* Terms and Conditions  */}

              <TermsAndConditionConatainer>
                <TermsAndConditionInnerConatainer>
                  <input type="checkbox" name="DonateBefore" value="yes" />
                  <label>
                    I agree &nbsp;
                    <a
                      href="https://www.google.com/"
                      style={{ textDecoration: "underline" }}
                    >
                      terms&condition
                    </a>
                  </label>
                </TermsAndConditionInnerConatainer>
              </TermsAndConditionConatainer>
            </form>
          </FormDetailsContainer>
        </BloodDonationContainer>
      </DonationOfMoneyForm>

      <RightArrowContainer>
        <img src="/DonateImages/Right-Arrow.png" alt="MyHospitalWebsite" />
      </RightArrowContainer>
    </MainContainer>
  );
}

// MainContainer For Dontains form

const MainContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  border: 2px solid pink;
  overflow:auto;
`;

// left arrow container

const LeftArrowContainer = styled.div`
  width: 5%;
  img {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    transition: all 0.3s ease-in-out;
    &:hover {
      cursor: pointer;
      transform: scale(1.08);
    }
  }
`;

// BloodDonation Form Container

const DonationOfBloodForm = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  width: 100%;
`;

const BloodDonationContainer = styled.div`
  box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  background-color: rgb(255, 255, 255);
  color: #090b13;
  width: 90%;
  border-radius: 4px;
`;

const ImgAndHeadingAndParagraphContainer = styled.div`
  display: flex;
  flex-direction: row;
  padding: 4px;
  margin: 3px;
`;
const HeadingAndDetailsContainer = styled.div`
  box-sizing: border-box;
  width: 100%;
  padding: 3px;
  margin: 1px;
`;

const MainHeadingContainer = styled.p`
  text-align: center;
  font-family: "Bree Serif", serif;
  font-size: 2rem;
  color: white;
  font-weight: bold;
  display: inline-flex;
  padding: 10px;
  color: #090b13;
  border-radius: 5px;
  word-spacing: 0.5rem;
  line-height: 1.4rem;
`;

const MainDetailsContainer = styled.p`
  text-align: center;
  color: #090b13;
  font-family: "Roboto", sans-serif;
  word-spacing: 0.5rem;
  line-height: 1.4rem;
  box-sizing: border-box;
  margin-top: 15px;
`;

const ImgContainer = styled.div`
  width: 18%;
  padding: 3px;
  margin: 1px;
  img {
    width: 100%;
    height: 100%;
  }
`;

// Form Details Container

const FormDetailsContainer = styled.div`
  margin-top: 3%;
`;

// Name container css

const NameContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-start;
`;

const InnerNameContainer = styled.div`
  margin: 4px;
  padding: 3px;
  width: 100%;

  input {
    font-size: 1.2rem;
    text-align: left;
    width: 100%;
    border-radius: 4px;
    font-family: "Open Sans", sans-serif;
    color: #090b13;
    border: 2px solid #525e8d;
    padding: 3px;
    cursor: pointer;
  }
  input:focus {
    outline: none;
    border: 2px solid #090b13;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  }
`;

// Phone number container code css

const PhoneNumberContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
`;

const CountryCodeContainer = styled.div`
  margin: 4px;
  padding: 3px;
  width: 20%;

  input {
    font-size: 1.2rem;
    text-align: left;
    width: 100%;
    border-radius: 4px;
    font-family: "Open Sans", sans-serif;
    color: #090b13;
    border: 2px solid #525e8d;
    padding: 3px;
    cursor: pointer;
  }
  input:focus {
    outline: none;
    border: 2px solid #090b13;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  }
`;

const PhoneContainer = styled.div`
  margin: 4px;
  padding: 3px;
  width: 100%;

  input {
    font-size: 1.2rem;
    text-align: left;
    width: 100%;
    border-radius: 4px;
    font-family: "Open Sans", sans-serif;
    color: #090b13;
    border: 2px solid #525e8d;
    padding: 3px;
    cursor: pointer;
  }
  input:focus {
    outline: none;
    border: 2px solid #090b13;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  }
`;

// css for blood group container

const QuestionLabel = styled.label`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  margin: 3px;
  p {
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    font-weight: bold;
    box-sizing: border-box;
    font-size: 1.3rem;
    padding: 4px;
  }
`;

const BloodGroupContainer = styled.div`
  display: flex;
  align-items: flex-start;
  justify-content: center;
  width: 100%;
`;

const PositiveBloodContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
`;

const NegativeBloodContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
`;

const InnerContainer = styled.div`
  display: flex;
  width: 92%;

  label {
    text-align: left;
    padding: 4px;
    margin: 3px;
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    box-sizing: border-box;
    font-size: 1.1rem;
  }

  input {
    padding: 2px;
    margin: 3px;
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    box-sizing: border-box;
    cursor: pointer;
  }
`;

// css for choice container
const ChoiceConatainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const ChoiceInnerConatainer = styled.div`
  width: 95%;
  display: flex;
  align-items: center;
  justify-content: flex-start;

  label {
    text-align: left;
    padding: 4px;
    margin: 3px;
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    box-sizing: border-box;
    font-size: 1.1rem;
  }
  input {
    padding: 2px;
    margin: 3px;
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    box-sizing: border-box;
    cursor: pointer;
  }
`;

// Terms and condition container
const TermsAndConditionConatainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  margin-top: 30px;
  margin-bottom: 2%;
`;

const TermsAndConditionInnerConatainer = styled.div`
  width: 99%;
  display: flex;
  align-items: center;
  justify-content: flex-start;

  label {
    text-align: left;
    padding: 4px;
    margin: 3px;
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    box-sizing: border-box;
    font-size: 1.2rem;
  }
  input {
    padding: 2px;
    margin: 3px;
    text-align: center;
    color: #090b13;
    font-family: "Roboto", sans-serif;
    word-spacing: 0.2rem;
    line-height: 1.4rem;
    box-sizing: border-box;
    cursor: pointer;
  }
`;

// MoneyDonation Form Container

const DonationOfMoneyForm = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;



// Right Arrow Container

const RightArrowContainer = styled.div`
  width: 5%;
  img {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    transition: all 0.3s ease-in-out;
    &:hover {
      cursor: pointer;
      transform: scale(1.08);
    }
  }
`;
