import React from "react";
import styled from "styled-components";

export default function DonationForm() {
  return (
    <MainContainer>
      <BloodDonationContainer>
        <ImgAndHeadingAndParagraphContainer>
          <ImgContainer>
            <img src="/DonateImages/BloodDontaionFormImage.png" alt="MyHospitalWebApp" />
          </ImgContainer>
          <HeadingAndDetailsContainer>
            <p>Blood Donation Form </p>
            <p>
              Lorem ipsum, dolor sit amet consectetur adipisicing elit. Porro
              quae pariatur modi tempore, enim, facilis nemo reprehenderit
              fugiat quo deleniti itaque inventore animi rem molestiae,
              voluptate in! Molestias nobis quaerat nemo placeat numquam eaque?
            </p>
          </HeadingAndDetailsContainer>
        </ImgAndHeadingAndParagraphContainer>
      </BloodDonationContainer>

      {/* <ImgContainer>
        <img src="/Images/HospitalLogo.png" alt="MyHospitalWebApp" />
      </ImgContainer>
      <FormContainer>
        <form>
          <UserName>
            <input type="text" placeholder="Enter First Name " />
            <input type="text" placeholder="Enter Second Name " />
          </UserName>
          <input type="text" placeholder="Enter Donor Name " />
        </form>
      </FormContainer> */}
    </MainContainer>
  );
}

const MainContainer = styled.div`
  border: 2px solid purple;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BloodDonationContainer = styled.div`
  border: 2px solid pink;
  width: 90%;
`;

const ImgAndHeadingAndParagraphContainer = styled.div`
  border: 2px solid grey;
  display: flex;
  flex-direction: row;
`;
const HeadingAndDetailsContainer = styled.div`
  border: 2px solid purple;
  box-sizing: border-box;
  width: 100%;
`;

const ImgContainer = styled.div`
  border: 2px solid pink;
  width: 18%;
  img {
    width: 100%;
    height: 100%;
    border: 2px solid purple;
  }
`;

