import React from "react";
import styled from "styled-components";
import InnerImgSlider from "../../innersmallslider/InnerImgSlider";
export default function TreatmentDetailsContainer() {
  let details = [
    {
      Treatment1: "Curative Treatment",
      Treamtent1Detail:
        "Curative or therapeutic care refers in part to treatments and therapies provided to a patient with the goal of curing an illness or condition. The terms are also used for treatments that delay disease progression even when a cure is not possible.",
      ExampleOfTreatmentAvailableHeading:
        "Example Of Curative Treamtent Available In Our Hospital",
      ExampleOfTreatmentAvailable: [
        {
          treatment: "Cancer Curative Treatment",
        },
        {
          treatment: "Sar Dard",
        },
        {
          treatment: "Stomatch Pain",
        },
        {
          treatment: "Brain",
        },
        {
          treatment: "Legs",
        },
        {
          treatment: "Hands",
        },
      ],
      data: [
        {
          img: "/Images/workforyou1.jpg",
          disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
        },
        {
          img: "/Images/workforyou2.jpg",
          disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
        },
        {
          img: "/Images/workforyou3.jpg",
          disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
        },
        {
          img: "/Images/workforyou4.jpg",
          disc: "Lorem ipsum dolor sit amet consectetur adipisicing elit.Eveniet iure rerum obcaecati et laborum earum!",
        },
      ],
    },
  ];

  const Patient = () => {
    alert("Patient Will Died");
  };
  return (
    <MainContainer>
      <TreatmentHeadingContainers>
        <p>Types Of Treatment</p>
      </TreatmentHeadingContainers>
      {details.map((item, index) => {
        return (
          <TreatmentDetails>
            <TreatmentHeading>
              <p>{item.Treatment1}</p>
            </TreatmentHeading>
            <TreatmentTypeDetails>
              <Treatmentinformation>
                <TreatmentInfo>
                  <p>{item.Treamtent1Detail}</p>
                </TreatmentInfo>
                <SliderContainer>
                  <InnerImgSlider data={item.data} index={index} />
                </SliderContainer>
              </Treatmentinformation>

              <ExampleTreatmentAvailableContainer>
                <ExampleTreatmentHeading>
                  <h2>{item.ExampleOfTreatmentAvailableHeading}</h2>
                </ExampleTreatmentHeading>
                <ExampleTreatmentList>
                  {item.ExampleOfTreatmentAvailable.map((x, i) => {
                    return <li key={i}>{x.treatment}</li>;
                  })}
                </ExampleTreatmentList>
              </ExampleTreatmentAvailableContainer>
            </TreatmentTypeDetails>
            <ButtonContainer>
              <button onClick={Patient}>view Details</button>
            </ButtonContainer>

            {/* 
              <TreatmentTypeDetails>
                <TreatmentInfo>
                  <p>{item.Treamtent1Detail}</p>
                </TreatmentInfo>
                <SliderContainer>
                  <InnerImgSlider data={item.data} index={index} />
                </SliderContainer>
              </TreatmentTypeDetails>
              <ExampleTreatmentAvailableContainer>
                <ExampleTreatmentHeading>
                  <h2>{item.ExampleOfTreatmentAvailableHeading}</h2>
                </ExampleTreatmentHeading>
                <ExampleTreatmentList>
                  {item.ExampleOfTreatmentAvailable.map((x, i) => {
                    return <li key={i}>{x.treatment}</li>;
                  })}
                </ExampleTreatmentList>
                <ButtonContainer onClick={Patient}>
                  view Details
                </ButtonContainer>
              </ExampleTreatmentAvailableContainer>
            </TreatmentIformation> */}
          </TreatmentDetails>
        );
      })}
    </MainContainer>
  );
}

const MainContainer = styled.div`
  width: 90%;
  position: relative;
  left: 80px;
  top: 40px;
  text-align: center;
  z-index: -1;
`;

const TreatmentHeadingContainers = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 3%;

  p {
    text-align: center;
    font-family: "Bree Serif", serif;
    font-size: 1.8rem;
    font-weight: bold;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
    background-color: white;
    color: #090b13;
    padding: 4px;
    margin-top: 30px;
    border-radius: 5px;
    /* 
    display: inline-flex;
    */
  }
`;

const TreatmentDetails = styled.div`
  box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  border-radius: 5px;
  background-color: white;
`;

const TreatmentHeading = styled.div`
  border: 2px solid green;
  display: flex;
  align-items: flex-start;
  margin: 2%;
  p {
    text-align: center;
    font-family: "Bree Serif", serif;
    font-size: 1.5rem;
    font-weight: bold;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
    background-color: white;
    color: #090b13;
    padding: 4px;
    margin-top: 30px;
    border-radius: 5px;
  }
`;

const TreatmentTypeDetails = styled.div`
  border: 2px solid grey;

  display: flex;
  flex-direction: row;
`;

const Treatmentinformation = styled.div`
  border: 2px solid purple;
  display: flex;
  flex-direction: column;
  width: 80%;
`;

// container for showing the meaning of the type of treatment
const TreatmentInfo = styled.div`
  border: 2px solid crimson;
  width: 100%;
`;

// container for image slider
const SliderContainer = styled.div`
  border: 2px solid navy;
  width: 100%;
`;

// container for Available treatment in our hospital

const ExampleTreatmentAvailableContainer = styled.div`
  border: 2px solid aquamarine;
  display: flex;
  flex-direction: column;
  box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px, rgba(0, 0, 0, 0.1) 0px 8px 16px;
  border-radius: 5px;
`;

const ExampleTreatmentHeading = styled.div`
  border: 2px solid aqua;
`;

const ExampleTreatmentList = styled.div`
  border: 2px solid pink;
`;

// button for view details
const ButtonContainer = styled.div`
  border: 2px solid red;
  margin-top: 2%;
  text-align: right;

  button {
    border: 2px solid red;
    position: relative;
    right: 7%;
    &:hover {
      transform: scale(1.06);
      cursor: pointer;
    }
  }
`;

