const ImgContainer = styled.div`
  width: 65%;
  transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
  position: relative;
  margin: 1.5%;

  img {
    object-fit: cover;
    opacity: 1;
    width: 100%;
    inset: 0px;
    position: relative;
    display: block;
    top: 0;
    border-radius: 5px;
  }

  video {
    width: 100%;
    position: absolute;
    top: 0px;
    opacity: 0;
    z-index: 0;
    border-radius: 5px;
    object-fit: cover;
  }
  &:hover {
    cursor: pointer;
    border-radius: 5px;
    transform: scale(1.01);
    img {
      opacity: 0;
    }
    video {
      opacity: 1;
      z-index: 1;
    }
  }
`;
