const MainContainer = styled.section`
  background-color: rgb(41, 40, 40);

  .main_container {
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .main-scroll-div {
    width: 90%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: space-between;
    /* margin-top: 2%; */
    border: 2px solid pink;
  }
  .cover {
    border: 2px solid green ;
    position: relative;
    width: 100%;
    height: 50%;
  }
`;

