import React from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import styled from "styled-components";
import Slider from "./Slider";
export default function ImageSlider() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  return (
    <Container>
      <h1>Recent Project</h1>
      <p>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Nam labore
        distinctio modi illum sint explicabo iusto blanditiis tempore quae fuga!
      </p>
      <Slide>
        <Slider>
          <div>Slider</div>;<div>Slider</div>;<div>Slider</div>;
          <div>Slider</div>;<div>Slider</div>;
        </Slider>
      </Slide>
    </Container>
  );
}

const Container = styled.div`
  position: relative;
  top: 100px;
  width: 80%;
  max-width: 1200px; //check
  border: 2px solid green;
  margin: 0 auto;
  padding: 3rem 0;
  text-align: center;

  h1 {
    font-size: 1.9rem;
  }

  p {
    width: 28rem;
    margin: 0 auto;
    padding: 1rem 0;
    font-size: 0.9rem;
  }
`;

